import React, { useState } from 'react';
import './Login.css'; // Make sure to create a corresponding CSS file

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Placeholder for future authentication logic
    alert(`Login attempted for ${username}`);
  };

  return (
    <div className="login-page">
      <h2>Login</h2>
      <form onSubmit={handleSubmit} className="login-form">
        <div className="input-group">
          <label htmlFor="username">Username</label>
          <input
            id="username"
            type="text"
            placeholder="E.g: Aleksandra"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="password">Password</label>
          <input
            id="password"
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="login-actions">
          <button type="submit" className="login-button">Log in as a Customer</button>
          <button type="submit" className="login-button">Log in as an Admin</button>
        </div>
        <div className="signup-link">
          New? <a href="/signup">Create an account!</a>
        </div>
      </form>
    </div>
  );
};

export default Login;
